import React, { useState } from "react";
import { AddWrapper, Containering, Wrappering } from "./NewCrudStyle";
import Dating from "./Dating";
const NewCrudHooks = () => {
  const [movies, setMovies] = useState(Dating);
  const [addInputValue, setAddInputValue] = useState("");
  const AddUser = () => {
    setMovies(
      [...movies, { id: movies.length + 1, name: addInputValue }],
      );
      setMovies = ""
  };
  return (
    <Wrappering>
      <Containering>
        <AddWrapper>
          <input
            value={value.name}
            onChange={(e) => setAddInputValue(e.target.value)}
            type="text"
          />{" "}
          <button onClick={AddUser}>Add</button>
        </AddWrapper>
        <table border={1} width="50%">
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Surname</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((value, index) => (
              <tr key={index}>
                <td>{value.id + 1}</td>
                <td>{value.name}</td>
                <td>{value.surname}</td>
                <td>{value.status}</td>
                <button>Delete</button>
                <button>Edit</button>
              </tr>
            ))}
          </tbody>
        </table>
      </Containering>
    </Wrappering>
  );
};

export default NewCrudHooks;
