import React, { useState, useReducer } from "react";

const MainRaducer = () => {
  const [counter, dispatch] = useReducer((state, action) => {
    console.log(state, action);
    switch (action.type) {
      case "plus":
        return state + 1;
      case "minus":
        return state - 1;
      case "five":
        return state + +action.payload;
      default:
        return state;
    }
  }, 0);
  return (
    <div>
      <h1>
        Counter : {counter}{" "}
        <select name="" id="">
          <option value="0">0</option>
          <option value="10">10</option>
          <option value="20">20</option>
          <option value="30">30</option>
          <option value="40">40</option>
        </select>
      </h1>
      <button onClick={() => dispatch({ type: "plus" })}>+</button>
      <button onClick={() => dispatch({ type: "minus" })}>-</button>
      <button onClick={() => dispatch({ type: "five" , payload :5})}>5</button>
    </div>
  );
};
export default MainRaducer;

// 1 tepada UseReducer qilib chaqrb olamz
// 2 useReducer deb state yaratb olamz va odatda ikkinchi qiymatimzni dispatch deb nomlab olamz
// 3 useReducerga qiymat beramz ikkita qiymat oladi birinchisi ()=> {} callback ikkinchisi , 0 inital yani boshlangich xolat
// 4 stateni qayerda ishlatmoqci bolsak osha yerga stateni opkelamz
// 5 dispatchni qayerda ishlatmoqci bolsak opkelb qoyamz funksiya qilib yani onclick bolganda detdgan joymzga
// 6 tepada callback funksiya oz ichiga state va actionni oladi  {{{{{{{{{{{ STATE BU COUNTGA TENG DISPATCH ESA PASDA BERIB YUBORILADIGA QIYMAT YANI TYPE :}}}}}}}}}}}
// 7 va pasda objectga orab dispatchda  biz type beramz yani biz dispatchni hammasiga beramz lekin typeni boshqa boshqa beramz
//  8 va biz typelarni teskhrshmz kerak buning uchun biz switch case ishlatamz yani switchcase biz action.type yani actionni typeni tekshramz
//  9 biz ikkinchi parametr jonatb yuborishmz ham mumkin yani pasda tepaga berb yuborgan qiymatmz yani typeni yonida payload berib yuboramz
//  10 va tepada statega biz actionni icidagi payloadni yozamz (action.payload)
