import React, { useContext } from "react";
import { Block, Block2 } from "./ContextStyle";
import Dates from "./contextData";
import { MainContext } from "./ParentContext";
export const Context = () => {
  const [state,setState] = useContext(MainContext)
  return (
    <Block>
      {Dates.map((value, id) => value.name.toLowerCase().includes(state.toLowerCase()) && (
        <Block2>
          <img src={value.url} alt="" />
          <h3>{value.name}</h3>
        </Block2>
      ))}
      {Dates.map((value, id) => value.id.toString().includes(state))}
    </Block>
  );
};

export default Context;
