import React, { useState } from "react";
import {
  AddWrapper,
  Button,
  Container,
  Img,
  Input,
  SearchMovies,
  TableBtnWrapper,
  Wrapper,
} from "./CrudStyle";
import Dates from "./data";
const FuncTable = () => {
  const [movies, setMovies] = useState(Dates);
    const [openinput, setOpenInput] = useState(false)
    const AddMovie = ()=>{
        setOpenInput(!openinput)
        console.log(openinput);
    }
  return (
    <Wrapper>
      <Container>
        <Button onClick={AddMovie} rang="green">AddMovies ‚úîÔ∏è</Button>
        <AddWrapper></AddWrapper>
        <SearchMovies>
            {openinput}
          <Input placeholder="üîç Search Movies" type="text" />
        </SearchMovies>
        <table border={1} width="50%">
          <thead>
            <tr>
              <td>Id</td>
              <td>Name</td>
              <td>Image</td>
              <td>Action</td>
            </tr>
          </thead>
          <tbody>
            {movies.map((value, index) => {
              return (
                <tr key={index}>
                  <td>{value.id}</td>
                  <td>
                    <Img src={value.url} />
                  </td>
                  <td>{value.name}</td>

                  <td>
                    <TableBtnWrapper>
                      <Button rang="red">delete ‚ùå</Button>
                      <Button rang="yellow">edit ‚úèÔ∏è</Button>
                    </TableBtnWrapper>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </Container>
    </Wrapper>
  );
};

export default FuncTable;
