import React, { useReducer, useState } from "react";

const UseReduceContext = () => {
  const [input, InputValue] =useState(0)
  const [counter, dispatch] = useReducer((state, action) => {
    console.log(state, action);
    switch (action.type) {
      case "plus":
        return state + 1;
      case "minus":
        return state - 1;
      case "five":
        return state + action.payload;
      default:
        return state;
    }
  }, 0);
  const [count, dispatchs] = useReducer((states, actions) => {
    switch (actions.types) {
      case "qoshish":
        return states + count;
      case " ayirish":
        return states - count;
        default :return states
    }
  });
  return (
    <div>
      <h1>Count : {counter}</h1>
      <button onClick={() => dispatch({ type: "plus" })}>+</button>
      <button onClick={() => dispatch({ type: "minus" })}>-</button>
      <button onClick={() => dispatch({ type: "five", payload: 50 })}>
        50
      </button>
      <button onClick={() => dispatch({ type: "five", payload: 10 })}>
        10
      </button>
      <br /> <br /> <br /> <br />
      <h2>Raqam :{count}</h2>
      <button onClick={() => dispatchs({ types: "qoshish" })}>+</button>
      <select onChange={(e) => count(e.target.value)} name="" id="">
        <option value="0">0</option>
        <option value="10">10</option>
        <option value="20">20</option>
        <option value="30">30</option>
        <option value="40">40</option>
      </select>
      <button onClick={() => dispatchs({ types: "ayirish" })}>-</button>
    </div>
  );
};
export default UseReduceContext;
