import styled from "styled-components";
export const Block = styled.div`
    border: 1px solid red;
    width: 100%;
    height: 85vh;
    background-color: black;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 30px;
    padding: 30px;

`
export  const Block2 =styled.div`
    width: 320px;
    height: 215px;
border: 1px solid red;
.img{
    width: 100%;
    height: 1;
}
`
