import React, { useState } from "react";
import { Container, TableBtnWrapper, Wrapper } from "./CrudStyle";
import Dates from "./data";
const FuncTable = () => {
  const [movies, setMovies] = useState(Dates);

  console.log(movies);
  return (
    <Wrapper>
      <Container>
        <table border={1} width="50%">
          <thead>
            <tr>
              <td>Id</td>
              <td>Name</td>
              <td>Image</td>
              <td>Action</td>
            </tr>
          </thead>
          <tbody>
            {movies.map((value, index) => {
              return (
                <tr key={index}>
                  <td>{value.id}</td>
                  <td>
                    <Img />
                  </td>
                  <td>{value.name}</td>

                  <td>
                    <TableBtnWrapper>
                      <button>delete ❌</button>
                      <button>edit ✏️</button>
                    </TableBtnWrapper>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </Container>
    </Wrapper>
  );
};

export default FuncTable;
