import React, { createContext, useState } from "react";

export const IndexMainContext = createContext();
const [state,setState] = useState("")
const IndexContext = ({ children }) => {
  return <IndexMainContext.Provider value={[state, setState]}>{children}</IndexMainContext.Provider>;
};
export default IndexContext;

