import React, { useState } from "react";
import {
  AddInputWrapper,
  AddWrapper,
  Button,
  Container,
  Img,
  Input,
  InputWrapper,
  SearchMovies,
  TableBtnWrapper,
  Wrapper,
} from "./CrudStyle";
import Dates from "./data";
const FuncTable = () => {
  const [movies, setMovies] = useState(Dates);
  const [openinput, setOpenInput] = useState(false);
  const [file, setFile] = useState();
  const [nameValue, setNameValue] = useState("");
  const [searchVal, SetsearchVal] = useState("");
  const [select, setSelect] = useState("")
  const AddImage = (e) => {
    setFile(URL.createObjectURL(e.target.files[0]));
  };
  const AddNewMovie = () => {
    setOpenInput(!openinput);
  };
  const DeleteMovie = (valueId) => {
    let NewMovie = movies.filter((value) => value.id !== valueId);
    setMovies(NewMovie);
  };
  const AddMovie = () => {
    setMovies([
      ...movies,
      { id: movies.length + 1, url: file, name: nameValue },
    ]);
    setNameValue("");
    setFile();
  };
  const EditMovie = (value)=>{
    console.log();
  }
  return (
    <Wrapper>
      <Container>
        {openinput ? (
          <Button onClick={AddMovie} rang="green">
            AddMovies ✔️
          </Button>
        ) : (
          <Button onClick={AddNewMovie} rang="green">
            NewMovies ✔️
          </Button>
        )}

        <AddInputWrapper>
          {openinput ? (
            <InputWrapper>
              <Input
                alt="Rasm Tanlamadiz"
                filename={file}
                type="file"
                placeholder="AddImage"
                onChange={AddImage}
              />
              <Input
                value={nameValue}
                onChange={({ target }) => setNameValue(target.value)}
                placeholder="AddName"
              />
            </InputWrapper>
          ) : (
            ""
          )}
        </AddInputWrapper>
        <SearchMovies>
          {openinput ? (
            ""
          ) : (
            <Input
              onChange={({ target }) => SetsearchVal(target.value)}
              placeholder="🔍 Search Movies"
              type="text"
            />
          )}
        </SearchMovies>
        <table border={1} width="50%">
          <thead>
            <tr>
              <td>Id</td>
              <td>Name</td>
              <td>Image</td>
              <td>Action</td>
            </tr>
          </thead>
          <tbody>
            {movies.map(
              (value, index) =>
                value.name.toLowerCase().includes(searchVal.toLowerCase()) && (
                  <tr key={index}>
                    <td>{value.id}</td>
                    <td>
                      <Img src={value.url} />
                    </td>
                    <td>{value.name}</td>

                    <td>
                      <TableBtnWrapper>
                        <Button
                          onClick={() => DeleteMovie(value.id)}
                          rang="red"
                        >
                          delete ❌
                        </Button>
                        <Button onClick={()=> EditMovie(value)} rang="yellow">edit ✏️</Button>
                      </TableBtnWrapper>
                    </td>
                  </tr>
                )
            )}
          </tbody>
        </table>
      </Container>
    </Wrapper>
  );
};

export default FuncTable;
