import React, { useContext } from "react";
import { Block, Block2 } from "./ContextStyle";
import Dates from "./contextData";
import { IndexMainContext } from "./indexContext";
export const Context = () => {
  const [state, setState] = useContext(IndexMainContext);
  return (
    <Block>
      {Dates.map((value) => value.name.includes(state) &&(
        <Block2><a href={value.link}>

        </a>

          <img src={value.url} alt="" />
          <h3>{value.name}</h3>
        </Block2>
      ))}
    </Block>
  );
};

export default Context;
