import React, { useState } from "react";
import { AddWrapper, Containering, Wrappering } from "./NewCrudStyle";
import Dating from "./Dating";
const NewCrudHooks = () => {
  const [movies, setMovies] = useState(Dating);
  const [addInputValue1, setAddInputValue1] = useState("");
  const [addInputValue2, setAddInputValue2] = useState("");
  const [addInputValue3, setAddInputValue3] = useState("");
  const AddUser = () => {
    setMovies([
      ...movies,
      { id: movies.length + 1, name: addInputValue1, surname: addInputValue2 },
    ]);
    setAddInputValue1("");
    setAddInputValue2("");
    setAddInputValue3("");
  };
  return (
    <Wrappering>
      <Containering>
        <AddWrapper>
          <input
            placeholder="Check Name"
            value={addInputValue1}
            onChange={(e) => setAddInputValue1(e.target.value)}
            type="text"
          />
          <input
            value={addInputValue2}
            onChange={(e) => setAddInputValue2(e.target.value)}
            type="text"
            placeholder=" Check Surname"
          />
          <input value={setAddInputValue3} onChange={(e)=> set} type="text" placeholder=" Check Status" />
          <button onClick={AddUser}>Add</button>
        </AddWrapper>
        <table border={1} width="50%">
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Surname</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((value, index) => (
              <tr key={index}>
                <td>{value.id + 1}</td>
                <td>{value.name}</td>
                <td>{value.surname}</td>
                <td>{value.status}</td>
                <button>Delete</button>
                <button>Edit</button>
              </tr>
            ))}
          </tbody>
        </table>
      </Containering>
    </Wrappering>
  );
};

export default NewCrudHooks;
